package javaAssignment;

public class Loop07 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Que.7:Write a program to print 1 to 10 using the do-while loop statement.
		 int i=1;    
		    do{    
		        System.out.println(i);    
		    i++;    
		    }while(i<=10); 
	}

}
