package operators;

public class Operator02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 
		//Que.2:Write a method for increment and decrement operators(++, --) 
		 
		 //for increment we use ++ to Increases the value of a variable by 1
		 int x = 5;
		    ++x;
		    System.out.println(x);
		  
		    //for decrement we use -- to Increases the value of a variable by 1
		    int c = 5;
		    --c;
		    System.out.println(c);
		    
		    
	}

}
