package operators;

public class Operator03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Que.3:Program to equal operator and not equal operators
	    
		  //for equal to operator we use == 
		    int d = 5;
		    int e = 3;
		    int f = 5;
		    System.out.println(d == e); // returns false because 5 is not equal to 3
		    System.out.println(d == f); // returns true because 5 is equal to 5
		    
		  //for equal to operator we use !=
		    int g = 5;
		    int h = 3;
		    int i = 5;
		    System.out.println(g != h); // returns true because 5 is not equal to 3
		    System.out.println(g != i); // returns false because 5 is  equal to 5
	}

}
